from django import formsclass ContactForm(forms.Form):    first_name = forms.CharField(        label='',        widget=forms.TextInput(            attrs={                "class": "form-control",                "placeholder": "First name"            }        )    )    last_name = forms.CharField(        label='',        widget=forms.TextInput(            attrs={                "class": "form-control",                "placeholder": "Last name"            }        ))    email = forms.EmailField(        label='',        widget=forms.TextInput(            attrs={                "class": "form-control",                "placeholder": "Email"            }        )    )    phone_number = forms.RegexField(regex=r'^\+?1?\d{9,15}$',        label='',        widget=forms.TextInput(            attrs={                "class": "form-control",                "placeholder": "Phone number"            }        )    )    marin_name = forms.CharField(        label='',        widget=forms.TextInput(            attrs={                "class": "form-control",                "placeholder": "Marine"            }        )    )    message = forms.CharField(        label='',        widget=forms.Textarea(            attrs={                "class": "form-control",                "placeholder": "Message",                "rows":2,                "cols":20            }        )    )    def clean_email(self):        email = self.cleaned_data.get('email')        if not "gmail.com" in email:            raise forms.ValidationError("Email not correct 'gmail.com' is needed. ")        return email    def clean_phone_number(self):        phone_number = self.cleaned_data.get("phone_number")        if not len(phone_number) == 10:            raise forms.ValidationError("10 digits is needed. ")        return phone_number