from django.http import HttpResponsefrom django.shortcuts import renderdef home_page(request):    context = {        "title": "Home page",        "content": "Welcome to HOME PAGE"    }    return render(request, 'home_page.html', context)def about_page(request):    context = {        "title": "About page",        "content": "Welcome to About page",        "tagsTest": "this is Tags test"    }    return render(request, 'home_page.html', context)def contact_page(request):    context = {        "title": "Contact page",        "content": "Welcome to Contact"    }    if request.method == "POST":        # print(request.POST)        print('fullname: ' + request.POST.get('fullname'))    return render(request, 'contact/view.html', context)