from django.http import HttpResponsefrom django.shortcuts import renderfrom .forms import ContactFormdef home_page(request):    context = {        "title": "Home page",        "content": "Welcome to HOME PAGE"    }    return render(request, 'home_page.html', context)def about_page(request):    context = {        "title": "About page",        "content": "Welcome to About page",        "tagsTest": "this is Tags test"    }    return render(request, 'home_page.html', context)def contact_page(request):    contact_form = ContactForm(request.POST or None)    context = {        "title": "Contact page",        "content": "Welcome to Contact",        "form": contact_form    }    if request.method == "POST":        if contact_form.is_valid():            print(contact_form.cleaned_data)        # print(request.POST)        # print('First name: ' + str(request.POST.get('first_name')))        # print('Last name: ' + str(request.POST.get('last_name')))        # print('Email: ' + str(request.POST.get('email')))        # print('Phone number: ' + str(request.POST.get('phone_number')))        # print('Marine name: ' + str(request.POST.get('marin_name')))        # print('Message: ' + str(request.POST.get('message')))    return render(request, 'contact/view.html', context)