from django.contrib.auth import authenticate, login, get_user_modelfrom django.http import HttpResponse, JsonResponsefrom django.shortcuts import render,redirectfrom .forms import ContactFormdef home_page(request):    context = {        "title": "Efficient Boat Rental",        "content": "Book your boat today!",    }    if request.user.is_authenticated:        context["premium_content"] = "Logged in"    return render(request, "home_page.html", context)def about_page(request):    context = {        "title":"About Page",        "content":" Welcome to the about page."    }    return render(request, "home_page.html", context)def contact_page(request):    contact_form = ContactForm(request.POST or None)    context = {        "title":"Contact",        "content":"Please add your message we'll be trying to answer a.s.a.p",        "form": contact_form    }    if contact_form.is_valid():        print(contact_form.cleaned_data)        if is_ajax(request=request):            return JsonResponse({"message": "Thanks you"})        if contact_form.errors:            errors = contact_form.errors.as_json()            if is_ajax(request=request):                return HttpResponse(errors, status=400, content_type='application/json')    # if request.method == "POST":    #     #print(request.POST)    #     print(request.POST.get('fullname'))    #     print(request.POST.get('email'))    #     print(request.POST.get('content'))    return render(request, "contact/view.html", context)def is_ajax(request):    return request.META.get('HTTP_X_REQUESTED_WITH') == 'XMLHttpRequest'